import React, {Component} from 'react';
import Coin from './Coin.js';
import {choice} from './helper.js'

class FlipCoin extends Component{
    constructor(props){
        super(props);
        this.state = {

            source: "/head.jpg",
            nFlips: 0,
            nHeads:0,
            nTails:0
        }
        this.handleClick = this.handleClick.bind(this);
    }

    handleClick(e){
        const newCoin = choice(["/head.jpg","/tail.jpg"]);
        this.setState(st => {
            // let newState = {
            //     ...st,
            //     source: newCoin,
            //     nFlips: st.nFlips +1,
            // }
            // if (newState.source === "/head.jpg"){
            //     newState.nHeads += 1;
            // }else{
            //     newState.nTails += 1;
            // }

            // return newState;

            return {
                source:newCoin,
                nFlips: st.nFlips + 1,
                nHeads: st.nHeads + (newCoin ==="/head.jpg" ? 1:0),
                nTails: st.nHeads + (newCoin ==="/head.jpg" ? 1:0)
            }
        })
    }

    render(){
        return(
            <div>
                <h1>Let's Flip a coin!</h1>
                <Coin source = {this.state.source}/>
                <button onClick = {this.handleClick}>Click me</button>
                <h1> Total {this.state.nFlips} Flips, {this.state.nHeads} Heads and {this.state.nTails} Tails!</h1>
            </div>
        );
    }
}

export default FlipCoin;
