import React, {Component} from 'react';
import Coin from './Coin.js';

class FlipCoin extends Component{
    constructor(props){
        super(props);
        this.state = {
            source: "/head.jpg"
        }
    }


    render(){
        return(
            <div>
                <h1>Let's Flip a coin</h1>
                <Coin source = {this.state.source}/>
                <button>Click me</button>
            </div>
        );
    }
}

export default FlipCoin;
